import { ethers } from "hardhat";
import * as fs from "fs";
import * as path from "path";

async function main\(\) {
    const addresses = JSON.parse\(fs.readFileSync\(path.resolve\(__dirname, "../deployment-addresses.json"\), "utf-8"\)\);
    const [deployer] = await ethers.getSigners\(\);

    const faucet = await ethers.getContractAt\(
        [
            "function setConfig\(address,uint256,uint256,uint256\) external",
            "function relayer\(\) view returns \(address\)",
            "function tokensPerClaim\(\) view returns \(uint256\)",
            "function ethPerClaim\(\) view returns \(uint256\)",
            "function cooldown\(\) view returns \(uint256\)"
        ],
        addresses.simpleBkcFaucet,
        deployer
    \);

    const relayer = await faucet.relayer\(\);
    const tokens = await faucet.tokensPerClaim\(\);
    const eth = await faucet.ethPerClaim\(\);
    const cd = await faucet.cooldown\(\);
    console.log\("Current config:"\);
    console.log\("  Relayer:", relayer\);
    console.log\("  Tokens/claim:", ethers.formatEther\(tokens\), "BKC"\);
    console.log\("  ETH/claim:", ethers.formatEther\(eth\), "ETH"\);
    console.log\("  Cooldown:", Number\(cd\)/3600, "hours"\);

    // Update: 100 BKC + 0.005 ETH, same relayer, 24h cooldown
    console.log\("\\nUpdating to 100 BKC + 0.005 ETH..."\);
    const tx = await faucet.setConfig\(
        relayer,
        ethers.parseEther\("100"\),
        ethers.parseEther\("0.005"\),
        86400
    \);
    await tx.wait\(\);
    console.log\("TX:", tx.hash\);

    const newTokens = await faucet.tokensPerClaim\(\);
    const newEth = await faucet.ethPerClaim\(\);
    console.log\("\\nNew config:"\);
    console.log\("  Tokens/claim:", ethers.formatEther\(newTokens\), "BKC"\);
    console.log\("  ETH/claim:", ethers.formatEther\(newEth\), "ETH"\);
}

main\(\).catch\(console.error\);
EOF)

