Bash(scripts/update_faucet_config.ts << 'EOF'
import { ethers } from "hardhat";
import * as fs from "fs";
import * as path from "path";

async function main\(\) {
    const addresses = JSON.parse\(fs.readFileSync\(path.resolve\(__dirname, "../deployment-addresses.json"\), "utf-8"\)\);
    const [deployer] = await ethers.getSigners\(\);

    const faucet = await ethers.getContractAt\(
        [
            "function setConfig\(address,uint256,uint256,uint256\) external",
            "function relayer\(\) view returns \(address\)",
            "function tokensPerClaim\(\) view returns \(uint256\)",
            "function ethPerClaim\(\) view returns \(uint256\)",
            "function cooldown\(\) view returns \(uint256\)"
        ],
        addresses.simpleBkcFaucet,
        deployer
    \);

    const relayer = await faucet.relayer\(\);
    // 100 years in seconds = 100 * 365.25 * 24 * 3600 = 3,155,760,000
    const HUNDRED_YEARS = 3155760000;
    console.log\("Setting: 1000 BKC + 0.01 ETH, 1 claim per wallet \(cooldown = 100 years\)..."\);
    const tx = await faucet.setConfig\(
        relayer,
        ethers.parseEther\("1000"\),
        ethers.parseEther\("0.01"\),
        HUNDRED_YEARS
    \);
    await tx.wait\(\);
    console.log\("TX:", tx.hash\);

    const cd = await faucet.cooldown\(\);
    console.log\("Cooldown:", Number\(cd\), "seconds \(~" + \(Number\(cd\) / 31557600\).toFixed\(0\) + " years\)"\);
    console.log\("Effectively: 1 claim per wallet forever"\);
}

main\(\).catch\(console.error\);
EOF)

Bash(1 <<'SCRIPT'
const faucetAddr = "0x9d75cfd52C855f94E35F9710cB894D209e192c82";
const abi = [
  "function relayer\(\) view returns \(address\)",
  "function paused\(\) view returns \(bool\)",
  "function canClaim\(address\) view returns \(bool\)",
  "function getFaucetStatus\(\) view returns \(uint256,uint256,uint256,uint256,uint256,uint256\)"
];
const faucet = new ethers.Contract\(faucetAddr, abi, ethers.provider\);
const relayer = await faucet.relayer\(\);
const paused = await faucet.paused\(\);
const status = await faucet.getFaucetStatus\(\);
console.log\("Relayer:", relayer\);
console.log\("Paused:", paused\);
console.log\("ETH balance:", ethers.formatEther\(status[0]\)\);
console.log\("BKC balance:", ethers.formatEther\(status[1]\)\);
console.log\("ETH per drip:", ethers.formatEther\(status[2]\)\);
console.log\("BKC per drip:", ethers.formatEther\(status[3]\)\);

// Test canClaim for a random address
const canClaim = await faucet.canClaim\("0x0000000000000000000000000000000000000001"\);
console.log\("canClaim\(0x01\):", canClaim\);
SCRIPT)

