Bash(1 <<'SCRIPT'
const { ethers } = require\("hardhat"\);
const [deployer] = await ethers.getSigners\(\);
const faucetAddr = "0xb80e5389b16693CAEe4655b535cc7Bceb4770255";
const abi = ["function setConfig\(uint256 _tokensPerDrip, uint256 _ethPerDrip, uint256 _cooldownPeriod\) external", "function getFaucetStatus\(\) view returns \(uint256,uint256,uint256,uint256,uint256,uint256\)", "function setRelayer\(address _relayer\) external"];
const faucet = new ethers.Contract\(faucetAddr, abi, deployer\);
// Set 1000 BKC + 0.01 ETH per claim, 100yr cooldown \(one-time\)
const tx1 = await faucet.setConfig\(ethers.parseEther\("1000"\), ethers.parseEther\("0.01"\), 3153600000\);
await tx1.wait\(\);
console.log\("Config set: 1000 BKC + 0.01 ETH"\);
// Set relayer to deployer \(for API\)
const tx2 = await faucet.setRelayer\(deployer.address\);
await tx2.wait\(\);
console.log\("Relayer set to deployer"\);
const s = await faucet.getFaucetStatus\(\);
console.log\("ETH:", ethers.formatEther\(s[0]\), "BKC:", ethers.formatEther\(s[1]\), "ethPerDrip:", ethers.formatEther\(s[2]\), "tokensPerDrip:", ethers.formatEther\(s[3]\)\);
SCRIPT)

